import { useState } from "react";

export const Formularios = () => {

    const [formulario, setformulario] = useState({
        email: 'test@test.com',
        password: '123456'
    })

    const onChange = (value:string, campo:string) => {
        setformulario({ ...formulario, [campo]: value });
    }

    return (
        <>
            <h3>Formulario</h3>
            <input type="text" className="form-control" placeholder="Email" value={formulario.email} onChange={({target})=> onChange(target.value, 'email')}></input>
            <input type="text" className="form-control mt-2 mb-2" placeholder="Password" value={formulario.password} onChange={({target})=> onChange(target.value, 'password')}></input>
        </>
    )
}
