import {useReducer} from 'react';

interface AuthState {
    validando: boolean,
    token: string | null,
    username: string,
    nombre: string,
}

const initialState: AuthState = { 
    validando: true, 
    token: null, 
    username: '', 
    nombre: '' 
}

type AuthAction = { type: 'logout'} 

const authReducer = (state: AuthState, action: AuthAction): AuthState => {
    switch (action.type) {
        case 'logout':
            return {
                validando: false,
                token: null,
                username: '',
                nombre: ''
            }
            break;
        default:
            return state;
            break;
    }
}

export const Login = () => {

    const [state, dispatch] = useReducer(authReducer, initialState)

    return (
        <>
            <h3>Login</h3>
            <div className="CRAlertInfo_Div">Validando Información...</div>
            <div className="CRAlertDanger_Div">No Autenticado...</div>
            <div className="CRAlertSuccess_Div">Autenticado...</div>
            <button className="CRLogin_Button">Login</button>    
            <button className="ficLogout_Button">Logout</button>
        </>
    )
}
