import { useEffect, useState, useRef } from "react";
import { reqRespApi } from '../api/reqRes';
import { ReqRespUsuarioListado, Usuario } from "../interfaces/reqResp";

export const Usuarios = () => {
    
    const [usuarios, setUsuarios] = useState<Usuario[]>([]);

    const CRRefPage = useRef(1);

    useEffect(() => {
        CRFnCargaUsuarios();
    }, [])

    const CRFnCargaUsuarios = async () => {
        const CRResponse = await
        reqRespApi.get<ReqRespUsuarioListado>('/users', {params: {
                page: ficRefPage.current
            }
        })
        .then(resp=> {
            setUsuarios(resp.data.data);
        })
        .catch(err => console.log(err))
    }

    const renderItem = (usuario: Usuario) => { 
        return ( 
            <tr key={usuario.id.toString()}> 
                <td> 
                    <img src={ usuario.avatar } alt={ usuario.first_name } style={{ width: 50, borderRadius: 100 }}></img>
                </td> 
                <td> 
                    { usuario.first_name } { usuario.last_name } 
                </td> 
                <td> 
                    { usuario.email } 
                </td> 
            </tr> 
        ) 
    }

    return (
        <> 
            <h3>Usuarios</h3> 
            <table className = "table"> 
                <thead> 
                    <tr> 
                        <th> 
                            Avatar 
                        </th> 
                        <th> 
                            Nombre 
                        </th> 
                        <th> 
                            Correo 
                        </th> 
                    </tr> 
                </thead> 
                <tbody>
                    {
                        usuarios.map(CRArgUsuario => renderItem(CRArgUsuario))
                    }
                </tbody>
            </table>
            <button className="btn btn-primary">Siguiente</button>
        </>
    )
}
